mapper.update:
update collection set status = #{status}, time = #{time}
where category_id = #{cid}
  and user_id = #{uid}

update hotel set collection_num =
     (
         select sum(status)
         from collection
         where category_id = #{cid}
     )
where id = #{id}

mapper.select:
select *
from collection
where category_id = #{cid}
  and user_id = #{uid}

select count(*)
from collection co,category c
where co.category_id = c.id
    and c.type = #{type}
    and co.user_id = #{uid}

select count(*)
from collection co,category c
where co.category_id = c.id
  and c.type = #{type}
  and co.user_id = #{uid}
  and to_days(co.time)=to_days(#{time})
