mapper.select:
select o.*
from
    `order` o
where o.user_id = #{uId}
    limit #{current} , #{size}

select o.*
from
    `order` o
where o.user_id = #{uId}
  and to_days(o.order_time)=to_days(#{time})
    limit #{current} , #{size}

select o.*
from
    `order` o,order_detail od,category c
where o.user_id = #{uId}
  and o.id = od.order_id
  and od.category_id = c.id
  and c.type = #{type}
    limit #{current} , #{size}

select o.*
from
`order` o,order_detail od,category c
where o.user_id = #{uId}
  and o.id = od.order_id
  and od.category_id = c.id
  and c.type = #{type}
  and to_days(o.order_time)=to_days(#{time})
    limit #{current} , #{size}

select o.*
from
    `order` o
where o.user_id = #{uId}
and o.status = #{status}
    limit #{current} , #{size}

select count(*)
from
    `order` o
where o.user_id = #{uId}
  and o.status = #{status}

select o.*
from
    `order` o
where o.user_id = #{uId}
  and to_days(o.order_time)=to_days(#{time})
  and o.status = #{status}
    limit #{current} , #{size}

