mapper.update:
update hotel set point =
    (
        select avg(point_num)
        from point
        where category_id = #{cid}
    )
where id = #{id}

update point set point_num = #{point}
where category_id = #{cid}
  and user_id = #{uid}

mapper.select:
select *
from point
where category_id = #{cid}
    and user_id = #{uid}
